mapper.update:
update provider
set
  PASSWORD = #{record.password,jdbcType=VARCHAR}
where
  CELLPHONE = #{record.cellphone,jdbcType=VARCHAR}

update provider
sname = #{sname,jdbcType=VARCHAR}, cellphone = #{cellphone,jdbcType=VARCHAR}, weixin = #{weixin,jdbcType=VARCHAR}, qq = #{qq,jdbcType=VARCHAR}, REGION_ID = #{regionId,jdbcType=VARCHAR}, emall = #{emall,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
		 SELECT  *  from provider


    		where  sname like concat('%',#{serviceName},'%')


      order by #{productExample.orderByClause}


	limit #{providerExample.pageStart},#{providerExample.pageSize}
